mapper.select:
SELECT hc.config_id, hc.config_number, hc.config_name, hc.company_id, hc.model_id, hc.column_name, hc.enable_flag, hc.description, hc.object_version_number, ct.company_full_name, dm.model_name FROM hpms_config hc, fnd_company_tl ct, hpms_data_model dm
1=1
and hc.model_id =#{modelId,jdbcType=DECIMAL}
and hc.config_number like concat('%',concat(#{configNumber,jdbcType=VARCHAR},'%'))
and hc.config_name like concat('%',concat(#{configName,jdbcType=VARCHAR},'%'))
and hc.config_id=#{configId,jdbcType=DECIMAL}
and hc.company_id =#{companyId,jdbcType=DECIMAL}
and hc.company_id = ct.company_id and dm.model_id = hc.model_id and ct.lang = 'zh_CN'

SELECT hc.company_id,
       hc.config_number,
       hc.config_id
  FROM hpms_config hc

      1=1

          and hc.company_id =#{companyId,jdbcType=DECIMAL}



          and hc.config_number=#{configNumber,jdbcType=VARCHAR}



          and hc.config_id!=#{configId,jdbcType=DECIMAL}

SELECT hc.config_id,
       hc.company_id,
       hc.model_id,
       hc.column_name
  FROM hpms_config hc

      1=1

          and hc.config_id!=#{configId,jdbcType=DECIMAL}



          and hc.company_id =#{companyId,jdbcType=DECIMAL}



          and hc.model_id=#{modelId,jdbcType=DECIMAL}



          and hc.column_name=#{columnName,jdbcType=VARCHAR}
