mapper.select:
SELECT HFMRH.READ_HIS_ID, HFMRH.COMPANY_ID, HFMRH.PROJECT_ID, HFMRH.EQUIPMENT_ID, HFMRH.INVOICE_CODE, HFMRH.LAST_READ, HFMRH.CURRENT_READ, HFMRH.METER_TOTAL_USE, HFMRH.METER_TOTAL_AMOUNT, HFMRH.READ_DATE, HFMRH.FLAG, HFMRH.NEW_RECORD_FLAG, HFMRH.IS_POSTED_FLAG, HFMRH.STATUS, HFMRH.PROGRAM_ID, HFMRH.REQUEST_ID, FCB.COMPANY_FULL_NAME, HMP.PROJECT_NAME, HME.EQUIPMENT_NAME, HCVV.MEANING STATUS_DESC, HME.EQUIPMENT_TYPE_ID, TO_CHAR(HFMRH.READ_DATE,'YYYY') YEAR, TO_CHAR(TO_NUMBER(TO_CHAR(HFMRH.READ_DATE,'MM'))) MONTH FROM HPMS_FIN_METER_READ_HIS HFMRH LEFT JOIN FND_COMPANY_B FCB ON HFMRH.COMPANY_ID = FCB.COMPANY_ID LEFT JOIN HPMS_MDM_PROJECT HMP ON HFMRH.PROJECT_ID = HMP.PROJECT_ID LEFT JOIN HPMS_MDM_EQUIPMENT HME ON HFMRH.EQUIPMENT_ID = HME.EQUIPMENT_ID LEFT JOIN HPMS_CODE_VALUE_V HCVV ON HCVV.CODE = 'HPMS_METER_ENTER_STATUS' AND HCVV.VALUE = HFMRH.STATUS AND HCVV.LANG = #{request.locale,jdbcType=VARCHAR} WHERE 1=1
AND HFMRH.EQUIPMENT_Id=#{equipmentId,jdbcType=DECIMAL}
AND HFMRH.NEW_RECORD_FLAG=#{newRecordFlag,jdbcType=VARCHAR}
and FCB.COMPANY_ID=#{companyId,jdbcType=DECIMAL}
and HMP.PROJECT_ID=#{projectId,jdbcType=DECIMAL}
and HME.EQUIPMENT_TYPE_ID=#{equipmentTypeId,jdbcType=DECIMAL}
and TO_CHAR(HFMRH.READ_DATE,'YYYY')=#{year,jdbcType=VARCHAR}
and TO_CHAR(TO_NUMBER(TO_CHAR(HFMRH.READ_DATE,'MM')))=#{month,jdbcType=VARCHAR}
